class EzHTTP{async get(t){const e=await fetch(t);return await e.json()}async post(t,e){const a=await fetch(t,{method:"POST",headers:{"Content-type":"application/json"},body:JSON.stringify(e)});return await a.json()}async put(t,e){const a=await fetch(t,{method:"PUT",headers:{"Content-type":"application/json"},body:JSON.stringify(e)});return await a.json()}async delete(t){const e=await fetch(t,{method:"DELETE",headers:{"Content-type":"application/json"}});return await"Resource Deleted... "+e.json()}}